__author__ = 'Michael Trotter (trotsky@gwu.edu)'

text = ""
cipher_text = ''

key = 3

for c in text:
    char_as_int = ord(c)
    char_as_int += key
    if char_as_int > 122:
        char_as_int -= 26
    cipher_text += chr(char_as_int)
    print("ORIGINAL CHAR: %s; ORDINAL %s; CHAR: %s" % (c, str(ord(c)), chr(char_as_int)))

print(cipher_text)